import React, { useEffect, useState } from 'react';
import Alert from 'react-bootstrap/Alert'
import { AiFillCheckCircle } from 'react-icons/ai';
import { BsInfoCircleFill } from 'react-icons/bs';
import { IoCloseCircle } from 'react-icons/io5';




const AlertMsg = ({variant, information}) => {

    function Icon() {
        if (variant === 'success') {
            return <AiFillCheckCircle size='23' className='alert-msg-icon' />
        }
        else if (variant === 'info') {
            return <BsInfoCircleFill size='23' className='alert-msg-icon' />
        }
        else if (variant === 'error') {
            return <IoCloseCircle className='alert-msg-icon' />
        }
        else
            return null
    }

    const [show, setShow] = useState(true)

    useEffect( () => {

        let countDown = setTimeout( () => {
            setShow(false);
        } , 5000)

        return () => {
            clearTimeout(countDown)
        }

    }, [])


    return (
            <Alert show={show} transition={null} id='alert' className={`alert-msg ${variant}`}>
                <Icon/>
                {information}
            </Alert>
    )
}

export default AlertMsg
