import React, { Fragment, useEffect, useState } from 'react';
import Navbar from '../NavBar/NavBar';
import BlockedProfile from './BlockedProfile';
import BlockedUser from './BlockedUser';
import AccessProfile from './AccessProfile';
import AlertMsg from '../AlertMsg/AlertMsg';






const MemberProfile = () => {

    useEffect( () => {
        document.title = 'Profil membre - Matcha'
    }, [])

// --------------------------------------------------------

    // PROFILE DISPLAY ↓↓↓
    const [profileStatus, setProfileStatus] = useState('302')


    const unblockConfirmation = () => {
        setProfileStatus('200')
        handleDisplayAlertMsg('Le profil a été bloqué')
    }


    function ProfileDisplay() {
        if (profileStatus === '302') {
            return <BlockedProfile onUnblockConfirmation={unblockConfirmation} />
        }
        else if (profileStatus === '403') {
            return <BlockedUser />
        }
        else if (profileStatus === '200') {
            return <AccessProfile onBlockingConfirmation={setProfileStatus} />
        }
    }


    // ALERT MSG ↓↓↓
    const [displayAlertMsg, setDisplayAlertMsg] = useState('hide')

    const handleDisplayAlertMsg = (information) => {
        setDisplayAlertMsg('display')
        setTimeout( () => {
            setDisplayAlertMsg('hide')
        } , 3000)
    }

    const [alertMsginfo, setAlertMsginfo] = useState(null)



    return (
        <Fragment>
            <Navbar />
            <AlertMsg variant='secondary' display={displayAlertMsg} information='Le profil a été bloqué' />
            <ProfileDisplay />
        </Fragment>
    )
}

export default MemberProfile;
