import React, { useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { v4 as uuidv4 } from 'uuid';

import { CSSTransition } from 'react-transition-group';




const Carousel$ = ({UserPhotos}) => {

    const controls = UserPhotos.length === 1 ? false : true ;

    const nodeRef = React.useRef(null);


    const [photo, setPhoto] = useState(0)
    const previousImg = () => {
        photo > 0 && photo <= (UserPhotos.length - 1) ?
        setPhoto(photo - 1) :
        setPhoto(UserPhotos.length - 1) ;
    }
    const nextImg = () => {
        photo >= 0 && photo < (UserPhotos.length - 1) ?
        setPhoto(photo + 1) :
        setPhoto(0) ;
    }

    return (
        // <Carousel nodeRef={nodeRef} fade={true} interval={null} indicators={false} controls={controls} className='carousel-div'>

        //     {UserPhotos.map( data => {
        //         return (
        //             <Carousel.Item ref={nodeRef} key={uuidv4()}>
        //                 <img className="d-block w-100" src={data} alt="Utilisateur" />
        //             </Carousel.Item>
        //         )
        //     })}

        // </Carousel>

        <div id="carouselExampleFade" className="carousel slide carousel-fade" data-bs-ride="carousel" data-interval="2000">
            <div className="carousel-inner embed-responsive-16by9">
                <div className="carousel-item embed-responsive-item active">
                    <img src={UserPhotos[photo]} className="d-block w-100" alt="user"/>
                </div>
                {UserPhotos.map( data => {
                return (
                    <div className="carousel-item embed-responsive-item " key={uuidv4()}>
                        <img src={data} className="d-block w-100" alt="user"/>
                    </div>
                )
                })}
            </div>
            <button onClick={previousImg} className="carousel-control-prev" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="prev">
                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            </button>
            <button onClick={nextImg} className="carousel-control-next" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="next">
                <span className="carousel-control-next-icon" aria-hidden="true"></span>
            </button>
        </div>

        // <Carousel>
        //     <Carousel.Item>
        //         <img className="d-block w-100" src={UserPhotos[0]} alt="First slide" />
        //     </Carousel.Item>
        //     <Carousel.Item>
        //         <img className="d-block w-100" src={UserPhotos[1]} alt="Second slide"/>
        //     </Carousel.Item>
        //     <Carousel.Item>
        //         <img className="d-block w-100" src={UserPhotos[2]} alt="Third slide"/>
        //     </Carousel.Item>
        // </Carousel>
    )
}


export default Carousel$;
