import React, { useState } from 'react';
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";


const Chat = () => {

    const [chatDrawer, setChatDrawer] = useState(false)

    const theChatDrawer = chatDrawer ? <FaChevronRight className='FaChevron' /> : <FaChevronLeft/>;

    return (
        <div className='chat'>
            {theChatDrawer}
        </div>
    )
}

export default Chat;
