const getCityFromCoordinates = (lat, lng) => {

        fetch(`https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lng}&format=json&accept-language=fr-FR`)
        .then(res => res.json())
        .then(
        (result) => {
            resultat = result;
        },
        (error) => {
            resultat = error;
        })

        return resultat;
}




