import React from 'react';
import Carousel from 'react-bootstrap/Carousel'




const Carousel$ = ({UserPhotos}) => {

    const controls = UserPhotos.length === 1 ? false : true ;

    const nodeRef = React.useRef(null);

    return (
        <Carousel fade={true} interval={null} indicators={false} controls={controls} className='carousel-div'>

            <Carousel.Item>
                <img className="d-block w-100" src={UserPhotos[0]} alt="Utilisateur" />
            </Carousel.Item>

            <Carousel.Item>
                <img className="d-block w-100" src={UserPhotos[1]} alt="Utilisateur" />
            </Carousel.Item>

            <Carousel.Item>
                <img className="d-block w-100" src={UserPhotos[2]} alt="Utilisateur" />
            </Carousel.Item>

        </Carousel>
    )
}


export default Carousel$;
