import { React, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import _markerIcon from '../../images/marker.png';






const Location = (props) => {

    const [center, setCenter] = useState({ lat: 48.862725, lng: 2.287592 })

    const markerIcon = new L.Icon({
        iconUrl: _markerIcon,
        iconSize: [45, 45],
        iconAnchor: [17, 46],
    });

    const containerMapStyle = {
        height: '300px',
        width: '64%',
        zIndex: '50'
    }


    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const getCity = () => {

        fetch("https://nominatim.openstreetmap.org/reverse?lat=48.862725&lon=2.287592&format=json&accept-language=<fr>")
        .then(res => res.json())
        .then(
        (result) => {
            setIsLoaded(true);
            setItems(result.items);
        },
        (error) => {
            setIsLoaded(true);
            setError(error);
        })

        if (error) {
            return <div>Erreur : {error.message}</div>;
        }
        else if (!isLoaded) {
            return <div>Chargement...</div>;
        }
        else {
            return (items)
        }
        }

    getCity();
    console.log(getCity);


    return (
        <MapContainer
            center={center}
            zoom={9}
            style={containerMapStyle}
        >
            <TileLayer
                url='https://api.maptiler.com/maps/basic/256/{z}/{x}/{y}.png?key=WzbI7kOa26WXiwQXx4jQ'
                // attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
            />
            <Marker
                position={[ 48.862725, 2.287592]}
                icon={markerIcon}
            >
                <Popup>Votre position</Popup>
            </Marker>
        </MapContainer>
    )

}

export default Location


