import React, { useState, useEffect, Fragment } from 'react';
import Navbar from '../NavBar/NavBar';
import UserInfoSection from './UserInfoSection'
import PasswordSection from './PasswordSection'
import AlertMsg from '../AlertMsg/AlertMsg';
import { NAMES_REGEX, USERNAME_REGEX, EMAIL_REGEX, PASSWORD_REGEX } from '../../other/Regex';
import { v4 as uuidv4 } from 'uuid';
import Alert from 'react-bootstrap/Alert'
import Form from 'react-bootstrap/Form'
import { RiUser3Fill } from 'react-icons/ri';
import { AiOutlineCheck } from 'react-icons/ai';



const Profile = () => {

    useEffect( () => {
        document.title = 'Profil - Matcha'
    }, [])

// --------------------------------------------------------

    /* modification of user information */
    const infoEdit$ = {
        lastname: false,
        firstname: false,
        username: false,
        email: false,
        password: false
    }

    const [infoEdit, setInfoEdit] = useState(infoEdit$)

    const handleModification = (idInfo, thisInfo) => {

        setInfoEdit(Object.assign(infoEdit, infoEdit$))
        setData(Object.assign(data, _data)) // /!\  À revoir. Lors de l'enregistrement de la nouvelle info puis le click sur 'annuler' ou 'modifier' l'info revient à l'origine.
        setErrorDatas(Object.assign(errorDatas, errorDatas$))
        setInfoEdit({...infoEdit, [idInfo]: !thisInfo})
    }

// --------------------------------------------------------

    // INFOS USER DATA ↓↓↓
    const _infoData = {
        lastname: 'Gadacha',
        firstname: 'Honsse',
        username: 'Username93',
        email: 'test@gmail.com'
    }

    const [usersPersonalInformation, UsersPersonalInformation] = useState(_data);

    const handleChange = e => {
        setData({...data, [e.target.id]: e.target.value});
    }

    // PASSWORD USER DATA 
    const _passwordData = {
        currentPassword: '',
        newPassword: '',
        newPasswordConfirmation: ''
    }

    const handleNewValues = (idInfo, thisInfo) => {
        setInfoEdit(Object.assign(infoEdit, infoEdit$))
        setErrorDatas(Object.assign(errorDatas, errorDatas$))
        setInfoEdit({...infoEdit, [idInfo]: !thisInfo})
    }

// --------------------------------------------------------

    const errorDatas$ = {
            infosError: false,
            passwordsError: false
    }

    const [errorDatas, setErrorDatas] = useState(errorDatas$)

// --------------------------------------------------------

    const userData = [
        {
            label: 'Nom',
            info: data.lastname,
            id: 'lastname',
            placeholder: '...',
            infoEdit: infoEdit.lastname,
            maxLength: "30"
        },
        {
            label: 'Prénom',
            info: data.firstname,
            id: 'firstname',
            placeholder: '...',
            infoEdit: infoEdit.firstname,
            maxLength: "30"
        },
        {
            label: 'Nom d\'utilisateur',
            info: data.username,
            placeholder: 'ex: pseudo, pseudo46, pseudo-46, pseudo_46 (15 car. max).',
            id: 'username',
            infoEdit: infoEdit.username,
            maxLength: "15"
        },
        {
            label: 'E-mail',
            info: data.email,
            placeholder: 'abc@exemple.com',
            id: 'email',
            infoEdit: infoEdit.email,
            maxLength: "250"
        }
    ]

    const passwordData = [
        {
            label: 'Actuel',
            id: 'currentPassword',
            placeholder: '...',
            maxLength: "250"
        },
        {
            label: 'Nouveau',
            id: 'newPassword',
            placeholder: '6 caract. min, 1 majusc., 1 chiffre et 1 caract. spécial.',
            maxLength: "250"
        },
        {
            label: 'Confirmation',
            id: 'newPasswordConfirmation',
            placeholder: '...',
            maxLength: "250"
        }
    ]

// ----------------------------PASSWORD CHECKING----------------------------

    const handleSubmitPassword = e => {
        e.preventDefault();

        if (currentPassword !== '' && newPassword !== '' && newPasswordConfirmation !== '') {
            if (currentPassword) { // if the currentPassword === userPassword
                if (PASSWORD_REGEX.test(newPassword) && newPassword === newPasswordConfirmation) {
                    handleModification();
                    document.body.scrollTop = document.documentElement.scrollTop = 0
                    // handleSuccess()
                    updateSuccessAlert();
                    console.log('"' + newPassword + '" is a new password ✓');

                } else {
                    setErrorDatas({...errorDatas, passwordsError: true})
                }
            } else {
                setErrorDatas({...errorDatas, passwordsError: true})
            }
        }
    }

// ----------------------------INFO CHECKING----------------------------

const handleSubmitInfo = e => {
    e.preventDefault();

    if (data.lastname !== '' && data.firstname !== '' && data.username !== '' && data.email !== '') {
        if (!NAMES_REGEX.test(data.lastname) || !NAMES_REGEX.test(data.firstname) || !USERNAME_REGEX.test(data.username) || !EMAIL_REGEX.test(data.email)) {
            setErrorDatas({...errorDatas, infosError: true})
        }
        else {
            handleNewValues();
            document.body.scrollTop = document.documentElement.scrollTop = 0;
            // handleSuccess()
            updateSuccessAlert();
            console.log(_data);
            console.log('"' + data.lastname + '"\n"' + data.firstname + '"\n"' + data.username+ '"\nis a new infos ✓');
        }
    }
}


    // ALERT ↓↓↓
    const [alertMessages, setAlertMessages] = useState([])


    const handleNewAlert = (newAlert) => {

        setAlertMessages(prevState => prevState.slice(1));
        setAlertMessages(prevState => [...prevState, newAlert]);
    }

    const updateSuccessAlert = () => {
        handleNewAlert({variant: "success",
                        information: "Vos informations ont été mis à jour."})
    }



    return (
        <Fragment>
            <Navbar />
            {alertMessages.map( alert => {
                return (
                    <AlertMsg
                        key={uuidv4()}
                        variant={alert.variant}
                        information={alert.information}
                    />
                )
            })}
            <div className='big-info-container centerElementsInPage'>
                <h2 className='personal-information'>Vos informations personelles</h2>
                <div className='info-container mb-2'>
                    <Form onSubmit={handleSubmitInfo}>
                        { userData.map( data => {
                            return (
                                <div key={uuidv4()}>
                                    <UserInfoSection 
                                        label={data.label}
                                        info={data.info}
                                        placeholder={data.placeholder}
                                        id={data.id}
                                        maxLength={data.maxLength}
                                        infoEdit={data.infoEdit}
                                        handleChange={handleChange}
                                        errorMsg={errorDatas.infosError}
                                    />
                                </div> )
                            })
                        }
                        <button type='submit' className='buttons-form-profile'>
                            Enregistrer
                        </button>
                    </Form>
                </div>
                <h2 className='personal-information'>Modifier votre mot de passe</h2>
                    <div className='info-container'>
                        <div>
                            <Form onSubmit={handleSubmitPassword}>
                            { passwordData.map( data => {
                                return (
                                    <div key={uuidv4()}>
                                        <PasswordSection
                                            label={data.label}
                                            placeholder={data.placeholder}
                                            id={data.id}
                                            maxLength={data.maxLength}
                                            handleChange={handleChange}
                                            errorMsg={errorDatas.passwordsError}
                                        />
                                    </div> )
                                })
                            }
                                <button type='submit' className='buttons-form-profile'>
                                    Enregistrer
                                </button>
                            </Form>
                        </div>
                    </div>
            </div>
        </Fragment>
    )
}

export default Profile
